import{_ as t,o as a,c as o,O as r}from"./chunks/framework.57763b9a.js";const i="/assets/01.920c9615.jpg",e="/assets/03.ff18fd2b.jpg",s="/assets/04.bab0cfa3.jpg",c="/assets/05.d597ff54.jpg",l="/assets/06.97c40e97.jpg",x=JSON.parse('{"title":"如何创建小程序项目","description":"","frontmatter":{},"headers":[],"relativePath":"pro-weixin/introduce/tips1/index.md","filePath":"pro-weixin/introduce/tips1/index.md","lastUpdated":1688802289000}'),d={name:"pro-weixin/introduce/tips1/index.md"},p=r('<h1 id="如何创建小程序项目" tabindex="-1">如何创建小程序项目 <a class="header-anchor" href="#如何创建小程序项目" aria-label="Permalink to &quot;如何创建小程序项目&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>小程序开发起步 <a href="https://developers.weixin.qq.com/community/business/course/000264e20a0dd8e69669b609451c0d" target="_blank" rel="noreferrer">小程序开发起步 | 微信开放社区 (qq.com)</a></p></div><h2 id="_1-注册小程序账号" tabindex="-1">1. 注册小程序账号 <a class="header-anchor" href="#_1-注册小程序账号" aria-label="Permalink to &quot;1. 注册小程序账号&quot;">​</a></h2><ol><li>在 <a href="https://mp.weixin.qq.com/" target="_blank" rel="noreferrer">微信公众平台</a> 注册小程序账号，账号类型选择【小程序】，然后通过 <code>邮箱注册（每个邮箱只能注册一个小程序）</code></li></ol><p><img src="'+i+'" alt="image"></p><hr><ol start="2"><li>填写完邮箱、密码等信息后查看注册邮箱的邮件，<code>点击邮件中的链接</code> 来选择小程序账号的主体并完成 <code>主体信息登记</code></li></ol><p><img src="'+e+'" alt="image"></p><hr><ol start="3"><li>登录到小程序的管理后台 <code>补充小程序的信息和设置小程序的类目</code>，设置小程序类目时需要管理员授权 <blockquote><p>注意：在设置小程序类目时不要选择游戏类型</p></blockquote></li></ol><p><img src="'+e+'" alt="image"></p><hr><ol start="4"><li>最后一步是获取小程序的 <code>AppID</code>，它是小程序的唯一标识</li></ol><p><img src="'+s+'" alt="image"></p><h2 id="_2-下载安装微信开发工具" tabindex="-1">2. 下载安装微信开发工具 <a class="header-anchor" href="#_2-下载安装微信开发工具" aria-label="Permalink to &quot;2. 下载安装微信开发工具&quot;">​</a></h2><p><a href="https://developers.weixin.qq.com/miniprogram/dev/devtools/stable.html" target="_blank" rel="noreferrer">下载</a> 并安装微信开发者工具，在使用微信开发者工具在前需要登录，打开微信微信扫码：</p><p><img src="'+c+'" alt="image"></p><h2 id="_3-搭建开发环境" tabindex="-1">3. 搭建开发环境 <a class="header-anchor" href="#_3-搭建开发环境" aria-label="Permalink to &quot;3. 搭建开发环境&quot;">​</a></h2><p>通过微信开发者工具来<code>创建并启动小程序项目</code>，小程序目录要求必须是<code>空白目录</code>，后端服务选择<code>不使用云服务</code></p><p><img src="'+l+'" alt="image"></p>',20),n=[p];function _(m,h,f,u,g,b){return a(),o("div",null,n)}const k=t(d,[["render",_]]);export{x as __pageData,k as default};
